mapper.select:
select p.p_name, p.p_price, cart_amount, pho.photo_id, ph.photo_oname,
pho.photo_sname, pho.photo_type, pho.photo_role
from cart, products p,
users u, photos pho
where user_id = #{user_id} and
cart.p_id = #{p.p_id}
and
p.p_id = #{pho.p_id}

mapper.update:
update cart
set
cart_amount=#{cart_amount}
where user_id=#{user_id} and
p_id=#{p_id}
